import axios from 'axios';
import router from "../../router";

axios.interceptors.request.use(
    config => {
        const storageData = JSON.parse(localStorage.getItem('user'));
        if (storageData) {
            config.headers['Authorization'] = storageData.token_type +' '+ storageData.access_token; 
        }
        return config;
    },
    error => {
        Promise.reject(error)
        //return error;
    });


// Add a response interceptor
axios.interceptors.response.use(
  async function (response) {
    return response;
  },
  async function (error) {
    await console.log("test error", error.response.status);
    await console.log("test error", error.response.data.message);
    if (error.response.status === 401) {
      router.push("/");
      return Promise.reject(error);
    }
    return Promise.reject(error);
  }
);


// initial state
const state = () => ({
  events: {},
  eventPaginatedData: {},
  createdEventData: {},
  messageEvent: null,
  event: null,
  updatedEventData: null,

});

// getters
const getters = {
  events: state => state.events,
  eventPaginatedData: state => state.eventPaginatedData,
  createdEventData: state => state.createdEventData,
  messageEvent: state => state.messageEvent,
  event: state => state.event,
  updatedEventData: state => state.updatedEventData,

};

// actions
const actions = {
  async fetchAllEvent({ commit }, query = null) {
    let page = 1;
    let search = '';
    if(query !== null){
      page = query?.page || 1;
      search = query?.search || '';
    }

    commit('setEventIsLoading', true);
    let url = `${process.env.VUE_APP_API_URL}events`;
    if (search === null) {
      url = `${url}`;
    } else {
      url = `${process.env.VUE_APP_API_URL}events/search?search=${search}&page=${page}`
    }

    await axios.get(url)
      .then(res => {
        const event = res.data.data;
        commit('setEvent', event);
        const pagination = {
          total: res.data.data.total,  // total number of elements or items
          per_page: res.data.data.per_page, // items per page
          current_page: res.data.data.current_page, // current page (it will be automatically updated when users clicks on some page number).
          total_pages: res.data.data.last_page // total pages in record
        }
        res.data.data.pagination = pagination;

        commit('setEventPaginated', res.data.data);

        commit('setEventIsLoading', false);
      }).catch(err => {
        commit('setEventIsLoading', false);
        commit('setMessage', err.response.data.message);

      });
  },

  async fetchDetailEvent({ commit }, id) {
    commit('setEventIsLoading', true);
    await axios.get(`${process.env.VUE_APP_API_URL}events/${id}`)
      .then(res => {
        commit('setEventDetail', res.data.data);
        commit('setEventIsLoading', false);
      }).catch(err => {
        commit('setEventIsLoading', false);
        commit('setMessage', err.response.data.message);
      });
  },

  async storeEvent({ commit }, event) {
    commit('setEventIsCreating', true);
    commit('setMessage', null);
    await axios.post(`${process.env.VUE_APP_API_URL}events/add_event`, event)
      .then(res => {
        commit('saveNewEvent', res.data);
        commit('setEventIsCreating', false);
      }).catch((err) => {
        commit('setEventIsCreating', false);
        if (err.response != null){ 
          commit('setMessage', err.response.data.message);
        }

      });
  },

  async updateEvent({ commit }, event) {
    commit('setEventIsUpdating', true);
    await axios.post(`${process.env.VUE_APP_API_URL}events/update_event/${event.id}?_method=PUT`, event)
      .then(res => {
        commit('setEventIsUpdating', false);
        commit('saveEventSchedule', res.data.data);
      }).catch(err => {
        commit('setEventIsUpdating', false);
        if (err.response != null){ 
          commit('setMessage', err.response.data.message);
        }
      });
  },

  async deleteEvent({ commit }, id) {
    commit('setEventIsDeleting', true);
    await axios.delete(`${process.env.VUE_APP_API_URL}events/delete_event/${id}`)
      .then(res => {
        commit('setDeleteEvent', res.data.data.id);
        commit('setEventIsDeleting', false);
      }).catch(err => {
        commit('setEventIsDeleting', false);
        if (err.response != null){ 
          commit('setMessage', err.response.data.message);
        }
      });
  },

  updateEventInput({ commit }, e) {
    commit('setEventDetailInput', e);
  }


}

// mutations
const mutations = {

  setEvent: (state, event) => {
    state.event = event
  },

  setEventPaginated: (state, eventPaginatedData) => {
    state.eventPaginatedData = eventPaginatedData;
  },

  setEventDetail: (state, event) => {
    state.event = event
  },

  setDeleteEvent: (state, id) => {
    let pData = state.eventPaginatedData.data;
    let index = pData.findIndex(pData => pData.id == id);
    state.eventPaginatedData.data.splice(index, 1);
    state.eventPaginatedData.data.filter(x => x.id !== id);
  },

  setEventDetailInput: (state, e) => {
    let event = state.event;
    event[e.target.title] = e.target.value;
    state.event = event
  },

  saveNewEvent: (state, event) => {
    state.createdEventData = event.data;
  },

  saveEventSchedule: (state, event) => {
    state.updatedEventData = event;
    // console.log(schedule);
    // console.log(state.updatedScheduleData );
  },

  setEventIsLoading(state, isLoading) {
    state.isLoading = isLoading
  },

  setEventIsCreating(state, isCreating) {
    state.isCreating = isCreating
  },

  setEventIsUpdating(state, isUpdating) {
    state.isUpdating = isUpdating
    state.updatedData = null;
  },

  setEventIsDeleting(state, isDeleting) {
    state.isDeleting = isDeleting
  },

  fromActionStatus(state, status) {
    state.formAction = status
  },

  setMessage(state, messageEvent){
    state.messageEvent = messageEvent
  },

}

export default {
  // namespaced: true,
  state,
  getters,
  actions,
  mutations
}
