import { createToast } from 'mosha-vue-toastify';
import 'mosha-vue-toastify/dist/style.css'; //import moment from 'moment';

import { Form, ErrorMessage, Field } from "vee-validate";
import { Cropper } from 'vue-advanced-cropper';
import 'vue-advanced-cropper/dist/style.css';
import * as yup from "yup";
import "@hennge/vue3-pagination/dist/vue3-pagination.css";
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import '@/assets/css/custom.css';
import $ from 'jquery';
export default {
  components: {
    Form,
    Field,
    ErrorMessage,
    Cropper
  },

  data() {
    const validationCreate = yup.object().shape({
      title: yup.string().required("Title is required!"),
      // album_id: yup
      // .string()
      // .required("Album is required!"),
      url: yup.string().required("Url is required!"),
      vedioStatus: yup.string().required("Status is required!")
    });
    return {
      editor: ClassicEditor,
      editorData: '<p>Content of the editor.</p>',
      editorConfig: {
        toolbar: {
          items: ['paragraph', 'heading', 'bold', 'italic', 'link', 'BulletedList', 'NumberedList', 'undo', 'redo', 'aignment']
        }
      },
      base_url: this.backendUrl,
      vedioForm: validationCreate,
      videos: {},
      vediosDetailsForModal: {},
      title: '',
      url: '',
      eventId: '',
      albumId: '',
      updateId: '',
      vedioStatus: 0,
      image: '',
      cropData: '',
      img: '',
      showImage: '',
      editImageFlag: false,
      formAction: 'insert',
      albumList: '',
      album_id: '',
      allVideo: '',
      order: 0,
      eventTitle: ''
    };
  },

  methods: {
    cancelUpdate() {
      location.reload();
    },

    //Print Vedio Details
    printVideoDetail() {
      $(document).ready(function () {
        $('#modalForPrintVideoDetail').modal('show');
      });
      $('#datatablePrint').DataTable().destroy();
      this.axios.get(this.base_url + "/api/getVideo").then(res => {
        this.allVideo = res.data.list;
        console.log(this.allVideo);
        $(document).ready(function () {
          $('#datatablePrint').DataTable({
            dom: '<"top"Bfi>rt<"top"lp><"clear">',
            buttons: [{
              extend: 'csv',
              messageTop: 'Video Informations',
              title: 'Asf 2022 Video Information Table'
            }, {
              extend: 'print',
              messageTop: 'Video Informations',
              title: 'Asf 2022 Video Information Table'
            }]
          });
          $('.buttons-print').each(function () {
            $(this).removeClass('btn-default').addClass('print-button');
          });
          $('.buttons-csv').each(function () {
            $(this).removeClass('btn-default').addClass('csv-button');
          });
          $('.dt-buttons').each(function () {
            $(this).css({
              float: 'left'
            });
          });
          $('.dataTables_length').each(function () {
            $(this).css({
              margin: '10px'
            });
          });
          $('#datatablePrint .dataTables_filter').each(function () {
            $(this).css({
              float: 'right'
            });
          });
        });
      });
    },

    //Get All Videos
    getVideos() {
      $('#datatable').DataTable().destroy();
      this.axios.get(this.base_url + "/api/getVideo").then(res => {
        this.videos = res.data.list;
        this.eventTitle = res.data.activeEvent.title;
        $(document).ready(function () {
          $('#datatable').DataTable();
        });
      });
    },

    onDetails(video) {
      this.vediosDetailsForModal = video;
      $(document).ready(function () {
        $('#modalForShowDetails').modal('show');
      });
    },

    onFileUpload(e) {
      let file = e.target.files[0];
      let reader = new FileReader();
      this.img = '';

      if (file["size"] < 10111775) {
        reader.onloadend = file => {
          console.log(file);
          this.img = reader.result;
        };

        reader.readAsDataURL(file);
      } else {
        alert("File size can not be bigger than 10 MB");
      }

      $('#remarkModal').modal('show');
    },

    //Croping image(Crop(image) -> Button) =>
    cropImage(e) {
      console.log(e);
      let reader = new FileReader();
      console.log(reader);
      this.image = this.cropData;
      var pic = this.image;
      $(document).ready(function () {
        $('#remarkModal').modal('hide');
        $('#prvImg').attr('src', pic);
      });
    },

    //Croping image params
    changes({
      coordinates,
      canvas
    }) {
      console.log(coordinates, canvas);
      this.cropData = canvas.toDataURL(this.image);
    },

    //Insert Data
    handleVideoInsert() {
      this.axios.post(this.base_url + '/api/addVideo', {
        title: this.title,
        url: this.url,
        album_id: this.album_id,
        thumbnail: this.image ? this.image : this.img,
        order: this.order,
        status: this.vedioStatus
      }).then(res => {
        console.log(res);
        createToast('Video Add Successfully !', {
          position: 'top-center',
          transition: 'bounce',
          timeout: 1500,
          type: 'success'
        });
        $(document).ready(function () {
          $('#remarkModal').modal('hide');
          $('#prvImg').attr('src', '../../assets/img/placeholder.png');
        });
        this.$refs.forminsert.resetForm();
        this.getVideos();
        setTimeout(function () {
          location.reload();
        }, 1000);
      }).catch(err => {
        console.log(err);
      });
    },

    // Update Data
    handleVideoUpdate() {
      this.axios.put(this.base_url + '/api/updateVideo/' + this.updateId, {
        title: this.title,
        url: this.url,
        event_id: this.eventId,
        album_id: this.album_id,
        thumbnail: this.image ? this.image : this.img,
        order: this.order,
        status: this.vedioStatus
      }).then(res => {
        console.log(res);
        createToast('Video Information Updated Successfully !', {
          position: 'top-center',
          transition: 'bounce',
          timeout: 1500,
          type: 'success'
        });
        this.$refs.formUpdate.resetForm();
        this.formAction = 'insert';
        $(document).ready(function () {
          $('#remarkModal').modal('hide');
          $('#prvImg').attr('src', '@/assets/img/placeholder.png');
        });
        this.getVideos();
        setTimeout(function () {
          location.reload();
        }, 1000);
      }).catch(err => {
        console.log(err);
      });
    },

    //Delete Specific Video
    onDelete(id) {
      this.axios.delete(this.base_url + '/api/deleteVideo/' + id).then(result => {
        console.log(result);
        createToast('Video Information Deleted Successfully !', {
          position: 'top-center',
          transition: 'bounce',
          timeout: 1500,
          type: 'success'
        });
        this.getVideos();
      }).catch(error => {
        console.log(error);
      });
    },

    onEdit(video) {
      this.updateId = video.id;
      this.formAction = 'update';
      this.editImageFlag = true;
      this.title = video.title;
      this.url = video.url;
      this.eventId = video.event_id;
      this.album_id = video.album_id;
      this.vedioStatus = video.status;
      this.showImage = this.base_url + '/public/uploads/images/gallery/videos/' + video.thumbnail;
    },

    getVideoAlbum() {
      this.axios.get(this.base_url + "/api/getvideoAlbums").then(res => {
        console.log(res.data.data);
        this.albumList = res.data.data;
      });
    }

  },

  created() {
    this.getVideos();
    this.getVideoAlbum();
  }

};
