import "jquery/dist/jquery.min.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import $ from "jquery";
import { Form, Field } from 'vee-validate';
import * as Yup from "yup";
import Multiselect from 'vue-multiselect';

// DataTable.use(DataTablesCore);
export default {
  components: {
    // DataTable
    Form,
    Field,
    Multiselect
  },
  data() {
    const storageData = JSON.parse(localStorage.getItem('user'));

    //  promotion: {
    //         employee_id: "",
    //         promotion_ref_number: "",
    //         promoted_designation: "",
    //         promotion_date: "",
    //         description: ""
    //     }
    const schema = Yup.object().shape({
      employee_id: Yup.string().required("Employee ID is required"),
      promotion_ref_number: Yup.string().required("Promotion Reference Number is required"),
      promoted_designation: Yup.string().required("Promoted Designation is required"),
      promotion_date: Yup.date().required("Promotion Date is required").typeError("Invalid date format"),
      description: Yup.string().required("Description is required")
    });
    return {
      schema,
      backend_url: process.env.VUE_APP_API_URL,
      storageData,
      user: "",
      departments: "",
      designations: "",
      // PromtotionType: "",
      office: "",
      url: this.backendUrl + "/api/users",
      basicInfo: {
        name: '',
        mobileNumber: '',
        email: '',
        designation: '',
        designation_id: '',
        image: ''
      },
      promotionRecordById: [],
      PromotionList: [],
      value: {
        id: '',
        emp: ''
      },
      employeeList: [],
      promotion: {
        employee_id: "",
        to_office: "",
        from_office: "",
        to_department: "",
        from_department: "",
        promotion_ref_number: "",
        to_designation: "",
        from_designation: "",
        promotion_date: "",
        description: ""
      },
      editpromotion: {
        employee_id: "",
        to_office: "",
        from_office: "",
        to_department: "",
        from_department: "",
        promotion_ref_number: "",
        to_designation: "",
        from_designation: "",
        promotion_date: "",
        description: ""
      }
    };
  },
  computed: {},
  methods: {
    clear() {
      console.log("Hi");

      // employee_id: "",
      //     to_office:"",
      //     from_office:"",
      //     to_department:"",
      //     from_department:"",
      //     promotion_ref_number: "",
      //     to_designation: "",
      //     from_designation:"",
      //     promotion_date: "",
      //     description: ""

      this.promotion.id = "";
      this.promotion.employee_id = "";
      this.promotion.to_office = "";
      this.promotion.from_office = "";
      this.promotion.to_department = "";
      this.promotion.from_department = "";
      this.promotion.promotion_ref_number = "";
      this.promotion.to_designation = "";
      this.promotion.from_designation = "";
      this.promotion.promotion_date = "";
      this.promotion.description = "";
      this.$refs.form.resetForm();
    },
    EditPromotion() {
      try {
        this.axios.post(this.backend_url + `updatePromotion/${this.editpromotion.id}`, this.editpromotion).then(res => {
          console.log(res);
          this.getPromotionList();
          $("#exampleModal1").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Update Data Successfully!`);
        }).catch(error => {
          console.error("An error occurred while updating the Promtotion record:", error);
          this.$toast.error(`Failed to update Promtotion record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to update Promtotion record.`);
      }
    },
    editbutton(id, item) {
      console.log(id);
      console.log(item);
      this.value.emp = item.employee_name;
      this.value.id = item.id;
      this.editpromotion.id = id;
      this.editpromotion.employee_id = item.employee_id;
      this.editpromotion.to_office = item.to_office;
      this.editpromotion.from_office = item.from_office;
      this.editpromotion.to_department = item.to_department;
      this.editpromotion.from_department = item.from_department;
      this.editpromotion.promotion_ref_number = item.promotion_ref_number;
      this.editpromotion.to_designation = item.to_designation;
      this.editpromotion.from_designation = item.from_designation;
      this.editpromotion.promotion_date = item.promotion_date;
      this.editpromotion.description = item.description;
    },
    deleterecord(id) {
      this.axios.delete(this.backend_url + 'deletePromotion/' + id).then(res => {
        console.log('Delete Successfully', res);
        this.getPromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Delete Successfully!!`);
      });
    },
    getPromotionList() {
      this.axios.get(this.backend_url + 'getPromotion?user_id=' + this.storageData.id).then(res => {
        console.log(res.data.list);
        this.PromotionList = res.data.list;
        setTimeout(function () {
          $("#datatable").DataTable({
            "processing": true
          });
        }, 100);
      }).catch(e => {
        console.log(e);
      });
    },
    viewDetails(id, item) {
      console.log(id);

      // console.log(response.data.data.designation_name);

      this.promotionRecordById.designation_name = item.designation_name;
      this.promotion.id = id;
      this.promotion.employee_name = item.employee_name;
      this.promotion.employee_id = item.employee_id;
      this.promotion.promotion_ref_number = item.promotion_ref_number;
      this.promotion.promoted_designation = item.designation_name;
      this.promotion.promotion_date = item.promotion_date;
      this.promotion.description = item.description;
    },
    nameWithLang({
      emp
    }) {
      return `${emp}`;
    },
    getEmployeeList() {
      this.axios.get(this.backend_url + 'employeeList').then(res => {
        console.log(res.data.employeeList);
        this.employeeList = res.data.employeeList;
        //   this.options =  res.data.employeeList;
      }).catch(e => {
        console.log(e);
      });
    },
    getDepartmentList() {
      this.axios.get(this.backend_url + 'getDepartment').then(res => {
        // console.log(res.data.data);
        this.departments = res.data.data;
      });
    },
    getTransfertype() {
      this.axios.get(this.backend_url + 'transferType').then(res => {
        this.transferType = res.data.list;
        // console.log('fddsfsdf');
        // console.log(this.transferType[0].title);
      });
    },

    getUser() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        // this.tansfer.employee_id = this.user
        // console.log(this.storageData.id);
      });
    },

    getDesignationList() {
      this.axios.get(this.backend_url + 'getDesignationMgt').then(res => {
        this.designations = res.data.data;
        // console.log(this.designations);
      });
    },

    getOffice() {
      this.axios.get(this.backend_url + `getOfficeMgt`).then(response => {
        this.office = response.data.list;
        // console.log(this.office);
      });
    },

    activestatus(id) {
      this.axios.patch(this.backend_url + `activePromotionRecord/${id}`).then(response => {
        console.log(response);
        this.getPromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.success(`Active status Successfully!!`);
      }).catch(error => {
        console.error(error);
      });
    },
    inactivestatus(id) {
      this.axios.patch(this.backend_url + `inactivePromotionRecord/${id}`).then(response => {
        console.log(response);
        this.getPromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Status Cancel!!`);
      }).catch(error => {
        console.error(error);
      });
    },
    savePromotion() {
      this.promotion.employee_id = this.value.id;
      try {
        // this.promotion.employee_id = this.storageData.id;
        this.axios.post(this.backend_url + 'addPromotion', this.promotion).then(res => {
          console.log(res);
          this.getPromotionList();
          $("#exampleModal").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Add Data Successfully!`);
          this.$refs.form.resetForm();
        }).catch(error => {
          console.error("An error occurred while saving the Promtotion record:", error);
          this.$toast.error(`Failed to save Promtotion record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to save Promtotion record.`);
      }
    }
  },
  created() {
    this.getEmployeeList();
    this.getPromotionList();
    this.getDepartmentList();
    this.getDesignationList();
    // this.getPromtotiontype();
    this.getUser();
    this.getOffice();
  },
  watch: {}
};
