import "jquery/dist/jquery.min.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import $ from "jquery";
import { Form, Field } from 'vee-validate';
import * as Yup from "yup";
import Multiselect from 'vue-multiselect';

// DataTable.use(DataTablesCore);
export default {
  components: {
    // DataTable
    Form,
    Field,
    Multiselect
  },
  data() {
    const storageData = JSON.parse(localStorage.getItem('user'));
    const schema = Yup.object().shape({
      leave_type: Yup.string().required("Leave Type is required"),
      from_date: Yup.date().required("From Date is required").typeError("Invalid date format"),
      to_date: Yup.date().required("To Date is required").typeError("Invalid date format"),
      day: Yup.number().required("Number of Days is required").typeError("Invalid number format"),
      description: Yup.string().required("Description is required")
    });
    return {
      schema,
      backend_url: process.env.VUE_APP_API_URL,
      storageData,
      user: "",
      departments: "",
      designations: "",
      transferType: "",
      office: "",
      url: this.backendUrl + "/api/users",
      basicInfo: {
        name: '',
        mobileNumber: '',
        email: '',
        designation: '',
        designation_id: '',
        image: ''
      },
      transferRecordById: [],
      transferList: [],
      LeaveRecordById: [],
      value: {
        id: '',
        emp: ''
      },
      employeeList: [],
      leave: {
        employee_id: "",
        leave_type: "",
        from_date: "",
        to_date: "",
        day: "",
        description: ""
      },
      editleave: {
        employee_id: "",
        leave_type: "",
        from_date: "",
        to_date: "",
        day: "",
        description: ""
      }
    };
  },
  computed: {},
  methods: {
    clear() {
      console.log("hi");
      this.$refs.form.resetForm();
    },
    EditTranfer() {
      try {
        this.axios.post(this.backend_url + `updateLeaveMgt/${this.editleave.id}`, this.editleave).then(res => {
          console.log(res);
          this.getTransferList();
          $("#exampleModal1").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Update Data Successfully!`);
        }).catch(error => {
          console.error("An error occurred while updating the Leave record:", error);
          this.$toast.error(`Failed to update Leave record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to update Leave record.`);
      }
    },
    editbutton(id, item) {
      console.log(id);
      this.value.id = item.id;
      this.value.emp = item.employee_name;
      this.axios.get(this.backend_url + 'specificUserLeave/' + id).then(response => {
        this.LeaveRecordById = response.data.data;
        console.log(this.LeaveRecordById);
        this.editleave.id = id;
        this.editleave.employee_name = this.LeaveRecordById.employee_name;
        this.editleave.employee_id = this.LeaveRecordById.employee_id;
        this.editleave.leave_type = this.LeaveRecordById.leave_type;
        this.editleave.from_date = this.LeaveRecordById.from_date;
        this.editleave.to_date = this.LeaveRecordById.to_date;
        this.editleave.day = this.LeaveRecordById.day;
        this.editleave.status = this.LeaveRecordById.status;
        this.editleave.description = this.LeaveRecordById.description;
      }).catch(e => {
        console.log(e);
      });
    },
    deleterecord(id) {
      this.axios.delete(this.backend_url + 'deleteLeaveMgt/' + id).then(res => {
        console.log('Delete Successfully', res.message);
        this.getTransferList();
        $('#datatable').DataTable().destroy();
        this.$toast.error("Delete Successfully");
      }).catch(error => {
        console.error("An error occurred while updating the transfer record:", error);
        this.$toast.error("An error occurred while updating the transfer record:", error);
      });
    },
    getTransferList() {
      this.axios.get(this.backend_url + 'getLeaveMgt?user_id=' + this.storageData.id).then(res => {
        console.log(res.data.list);
        this.transferList = res.data;
        setTimeout(function () {
          $("#datatable").DataTable({
            "processing": true
          });
        }, 100);
      }).catch(e => {
        console.log(e);
      });
    },
    getDays() {
      const fromDate = new Date(this.leave.from_date);
      const toDate = new Date(this.leave.to_date);
      console.log(fromDate, toDate);
      // Check if either of the dates is invalid
      if (this.leave.from_date === "" || this.leave.to_date === "") {
        this.leave.day = 0;
      } else {
        const timeDifference = toDate - fromDate;
        const daysDifference = timeDifference / (1000 * 3600 * 24);

        // Ensure the result is a positive whole number
        this.leave.day = Math.abs(Math.round(daysDifference));
      }
    },
    deletebutton(id) {
      this.axios.delete(this.backend_url + 'deleteTransferRecord/' + id).then(res => {
        console.log('Delete Successfully', res);
        this.getTransferList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Delete Successfully!!`);
      });
    },
    viewDetails(id, item) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificUserLeave/' + id).then(response => {
        console.log(response);
        this.LeaveRecordById = response.data.data;
        console.log(this.LeaveRecordById.Leave_type);
        this.leave.id = id;
        this.leave.employee_name = item.employee_name;
        this.leave.employee_id = this.LeaveRecordById.employee_id;
        this.leave.leave_type = this.LeaveRecordById.leave_type;
        this.leave.from_date = this.LeaveRecordById.from_date;
        this.leave.to_date = this.LeaveRecordById.to_date;
        this.leave.day = this.LeaveRecordById.day;
        this.leave.description = this.LeaveRecordById.description;
      }).catch(e => {
        console.log(e);
      });
    },
    nameWithLang({
      emp
    }) {
      return `${emp}`;
    },
    getEmployeeList() {
      this.axios.get(this.backend_url + 'employeeList').then(res => {
        console.log(res.data.employeeList);
        this.employeeList = res.data.employeeList;
        //   this.options =  res.data.employeeList;
      }).catch(e => {
        console.log(e);
      });
    },
    getDepartmentList() {
      this.axios.get(this.backend_url + 'getDepartment').then(res => {
        // console.log(res.data.data);
        this.departments = res.data.data;
      });
    },
    getTransfertype() {
      this.axios.get(this.backend_url + 'transferType').then(res => {
        this.transferType = res.data.list;
        // console.log('fddsfsdf');
        // console.log(this.transferType[0].title);
      });
    },

    getUser() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        // this.tansfer.employee_id = this.user
        // console.log(this.storageData.id);
      });
    },

    getDesignationList() {
      this.axios.get(this.backend_url + 'getDesignationMgt').then(res => {
        this.designations = res.data.data;
        // console.log(this.designations);
      });
    },

    getOffice() {
      this.axios.get(this.backend_url + `getOfficeMgt`).then(response => {
        this.office = response.data.list;
        // console.log(this.office);
      });
    },

    activestatus(id) {
      this.axios.patch(this.backend_url + `activeLeaveMgtRecord/${id}`).then(response => {
        console.log(response);
        this.getTransferList();
        $('#datatable').DataTable().destroy();
        this.$toast.success(`Active status Successfully!!`);
      }).catch(error => {
        console.error(error);
      });
    },
    inactivestatus(id) {
      this.axios.patch(this.backend_url + `inactiveLeaveMgtRecord/${id}`).then(response => {
        console.log(response);
        this.getTransferList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Status Cancel!!`);
      }).catch(error => {
        console.error(error);
      });
    },
    getLeavetype() {
      this.axios.get(this.backend_url + 'getLeaveType').then(res => {
        this.LeaveType = res.data.list;
        console.log(this.LeaveType);
      });
    },
    saveLeave() {
      this.leave.employee_id = this.value.id;
      try {
        // this.leave.employee_id = this.storageData.id;
        this.axios.post(this.backend_url + 'addLeaveMgt', this.leave).then(res => {
          console.log(res);
          this.getTransferList();
          $("#exampleModal").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Add Data Successfully!`);
          this.$refs.form.resetForm();
        }).catch(error => {
          console.error("An error occurred while saving the Leave record:", error);
          this.$toast.error(`Failed to save Leave record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to save Leave record.`);
      }
    }
  },
  created() {
    this.getTransferList();
    this.getLeavetype();
    this.getUser();
    this.getEmployeeList();
  },
  watch: {}
};
