import "jquery/dist/jquery.min.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import $ from "jquery";
import { Form, Field } from 'vee-validate';
import * as Yup from "yup";
import Multiselect from 'vue-multiselect';

// DataTable.use(DataTablesCore);
export default {
  components: {
    // DataTable,
    Form,
    Field,
    Multiselect
  },
  data() {
    const storageData = JSON.parse(localStorage.getItem('user'));
    const schema = Yup.object().shape({
      transfer_type: Yup.string().required("Transfer Type is required"),
      to_department: Yup.string().required("To Department is required"),
      from_department: Yup.string().required("From Department is required"),
      transfer_date: Yup.date().required("Transfer Date is required").typeError("Invalid date format"),
      transfer_order_number: Yup.string().required("Transfer Order Number is required"),
      to_designation: Yup.string().required("To Designation is required"),
      from_designation: Yup.string().required("From Designation is required"),
      join_date: Yup.date().required("Join Date is required").typeError("Invalid date format"),
      to_office: Yup.string().required("To Office is required"),
      from_office: Yup.string().required("From Office is required"),
      transfer_order: Yup.string().required("Transfer Order is required"),
      transfer_letter: Yup.string().required("Transfer Letter is required")
    });
    return {
      schema,
      backend_url: process.env.VUE_APP_API_URL,
      storageData,
      user: "",
      departments: "",
      designations: "",
      transferType: "",
      office: "",
      pdf: [],
      url: this.backendUrl + "/api/users",
      basicInfo: {
        name: '',
        mobileNumber: '',
        email: '',
        designation: '',
        designation_id: '',
        image: ''
      },
      value: {
        id: '',
        emp: ''
      },
      employeeList: [],
      transferRecordById: [],
      AcrList: [],
      Acr: {
        employee_id: "",
        emp_name: "",
        remarks: "",
        designation: "",
        department: "",
        office_name: "",
        rack_number: "",
        bin_number: "",
        file: "",
        file_number: "",
        acr_year: "",
        score: ""
      },
      editAcr: {
        employee_id: "",
        emp_name: "",
        remarks: "",
        designation: "",
        department: "",
        office_name: "",
        rack_number: "",
        bin_number: "",
        file: "",
        file_number: "",
        acr_year: "",
        score: ""
      }
    };
  },
  computed: {},
  methods: {
    handleFileChange(event) {
      const selectedFile = event.target.files[0];
      this.Acr.transfer_letter = selectedFile;
    },
    edithandleFileChange(event) {
      const selectedFile = event.target.files[0];
      this.editAcr.transfer_letter = selectedFile;
    },
    editbutton(id) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificAcrInfo/' + id).then(response => {
        this.value.id = response.data.data.employee_id;
        this.value.emp = response.data.data.name;
        this.editAcr.employee_id = response.data.data.employee_id, this.editAcr.emp_name = response.data.data.emp_name, this.editAcr.remarks = response.data.data.remarks, this.editAcr.designation = response.data.data.designation, this.editAcr.department = response.data.data.department, this.editAcr.office_name = response.data.data.office_name, this.editAcr.rack_number = response.data.data.rack_number, this.editAcr.bin_number = response.data.data.bin_number, this.editAcr.file = response.data.data.file, this.editAcr.file_number = response.data.data.file_number, this.editAcr.acr_year = response.data.data.acr_year, this.editAcr.score = response.data.data.score;
      }).catch(e => {
        console.log(e);
      });
    },
    deletebutton(id) {
      this.axios.delete(this.backend_url + 'deleteAcrInfo/' + id).then(res => {
        console.log('Delete Successfully', res);
        this.getAcrList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Delete Successfully!!`);
      });
    },
    viewDetails(item) {
      this.value.id = item.employee_id;
      this.value.emp = item.name;
      this.editAcr.employee_id = item.employee_id, this.Acr.name = item.name, this.Acr.remarks = item.remarks, this.Acr.designation = item.designation, this.Acr.department = item.department, this.Acr.office_name = item.office_name, this.Acr.rack_number = item.rack_number, this.Acr.bin_number = item.bin_number, this.Acr.file = item.file, this.Acr.file_number = item.file_number, this.Acr.acr_year = item.acr_year, this.Acr.score = item.score;
    },
    getAcrList() {
      this.axios.get(this.backend_url + 'getAcr').then(res => {
        console.log(res.data.list);
        this.AcrList = res.data.list;
        setTimeout(function () {
          $("#datatable").DataTable({
            "processing": true
          });
        }, 100);
      }).catch(e => {
        console.log(e);
      });
    },
    getDepartmentList() {
      this.axios.get(this.backend_url + 'getDepartment').then(res => {
        // console.log(res.data.data);
        this.departments = res.data.data;
      });
    },
    getDesignationList() {
      this.axios.get(this.backend_url + 'getDesignationMgt').then(res => {
        this.designations = res.data.data;
        // console.log(this.designations);
      });
    },

    getTransfertype() {
      this.axios.get(this.backend_url + 'transferType').then(res => {
        this.transferType = res.data.list;
        // console.log('fddsfsdf');
        // console.log(this.transferType[0].title);
      });
    },

    getUser() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        this.Acr.employee_id = this.user;
        // console.log(this.storageData.id);
      });
    },

    getOffice() {
      this.axios.get(this.backend_url + `getOfficeMgt`).then(response => {
        this.office = response.data.list;
        // console.log(this.office);
      });
    },

    saveTranfer() {
      let formData = new FormData();
      formData.append('employee_id', this.value.id);
      formData.append('remarks', this.Acr.remarks);
      formData.append('designation', this.Acr.designation);
      formData.append('department', this.Acr.department);
      formData.append('office_name', this.Acr.office_name);
      formData.append('rack_number', this.Acr.rack_number);
      formData.append('bin_number', this.Acr.bin_number);
      formData.append('file', this.Acr.file);
      formData.append('file_number', this.Acr.file_number);
      formData.append('acr_year', this.Acr.acr_year);
      formData.append('score', this.Acr.score);
      try {
        // this.Acr.employee_id = this.storageData.id;
        this.axios.post(this.backend_url + 'addacr', formData).then(res => {
          console.log(res);
          this.getAcrList();
          $("#exampleModal").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Add Data Successfully!`);
          this.$refs.form.resetForm();
        }).catch(error => {
          console.error("An error occurred while saving the transfer record:", error);
          this.$toast.error(`Failed to save transfer record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to save transfer record.`);
      }
    },
    EditTranfer() {
      let formData = new FormData();
      formData.append('employee_id', this.editAcr.employee_id);
      formData.append('remarks', this.editAcr.remarks);
      formData.append('designation', this.editAcr.designation);
      formData.append('department', this.editAcr.department);
      formData.append('office_name', this.editAcr.office_name);
      formData.append('rack_number', this.editAcr.rack_number);
      formData.append('bin_number', this.editAcr.bin_number);
      formData.append('file', this.editAcr.file);
      formData.append('file_number', this.editAcr.file_number);
      formData.append('acr_year', this.editAcr.acr_year);
      formData.append('score', this.editAcr.score);
      try {
        this.axios.post(this.backend_url + `updateTransferRecord/${this.editAcr.id}`, formData).then(res => {
          console.log(res);
          this.getAcrList();
          $("#exampleModal1").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Update Data Successfully!`);
        }).catch(error => {
          console.error("An error occurred while updating the transfer record:", error);
          this.$toast.error(`Failed to update transfer record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to update transfer record.`);
      }
    },
    nameWithLang({
      emp
    }) {
      return `${emp}`;
    },
    getEmployeeList() {
      this.axios.get(this.backend_url + 'employeeList').then(res => {
        console.log(res.data.employeeList);
        this.employeeList = res.data.employeeList;
        //   this.options =  res.data.employeeList;
      }).catch(e => {
        console.log(e);
      });
    }
  },
  created() {
    this.getAcrList();
    this.getDepartmentList();
    this.getDesignationList();
    this.getTransfertype();
    this.getUser();
    this.getOffice();
    this.getEmployeeList();
  },
  mounted() {},
  watch: {}
};
