    async captureAndExportToPDF() {
      // Find the element to capture by its ID
      const elementToCapture = document.getElementById("screenshot-element");
      if (!elementToCapture) {
        alert("Element not found.");
        return;
      }
      try {
        // Capture the element as a canvas
        const canvas = await html2canvas(elementToCapture);

        // Create a new PDF document
        const pdf = new jsPDF({
          orientation: "portrait",
          // or "landscape"
          unit: "mm",
          format: "a4" // or other desired formats
        });

        // Calculate the width and height for the PDF
        const pdfWidth = pdf.internal.pageSize.getWidth();
        const pdfHeight = pdf.internal.pageSize.getHeight();
        const ratio = pdfWidth / canvas.width;
        const canvasImageWidth = canvas.width * ratio;
        const canvasImageHeight = canvas.height * ratio;

        // Add the captured canvas as an image in the PDF
        pdf.addImage(canvas, "PNG", 0, 0, canvasImageWidth, canvasImageHeight);

        // Save the PDF or open it in a new tab
        pdf.save("screenshot.pdf");
      } catch (error) {
        console.error("Error capturing or exporting to PDF:", error);
