import "jquery/dist/jquery.min.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import $ from "jquery";
import { Form, Field } from 'vee-validate';
import * as Yup from "yup";
import Multiselect from 'vue-multiselect';

// DataTable.use(DataTablesCore);
export default {
  components: {
    // DataTable
    Form,
    Field,
    Multiselect
  },
  data() {
    const storageData = JSON.parse(localStorage.getItem('user'));
    const schema = Yup.object().shape({
      employee_id: Yup.string().required("Employee ID is required"),
      training_center_name: Yup.string().required("Training Center Name is required"),
      training_name: Yup.string().required("Training Name is required"),
      description: Yup.string().required("Description is required"),
      // Corrected error message
      training_score: Yup.string().required("Training Score is required"),
      training_feedback: Yup.string().required("Training Feedback is required"),
      training_start_date: Yup.date().required("Training Start Date is required").typeError("Invalid date format"),
      training_strt_date: Yup.date().required("Training End Date is required").typeError("Invalid date format"),
      training_end_date: Yup.date().required("Training End Date is required").typeError("Invalid date format")
    });
    return {
      schema,
      backend_url: process.env.VUE_APP_API_URL,
      storageData,
      employeeList: [],
      user: "",
      departments: "",
      designations: "",
      transferType: "",
      office: "",
      edit: false,
      url: this.backendUrl + "/api/users",
      basicInfo: {
        name: '',
        mobileNumber: '',
        email: '',
        designation: '',
        designation_id: '',
        image: ''
      },
      transferRecordById: [],
      promotionList: [],
      training: {
        employee_id: "",
        training_center_name: "",
        training_name: "",
        description: "",
        training_score: "",
        training_feedback: "",
        training_strt_date: "",
        training_end_date: ""
      },
      value: {
        name: 'Vue.js',
        language: 'JavaScript'
      },
      options: [{
        name: 'Vue.js',
        language: 'JavaScript'
      }, {
        name: 'Rails',
        language: 'Ruby'
      }, {
        name: 'Sinatra',
        language: 'Ruby'
      }, {
        name: 'Laravel',
        language: 'PHP'
      }, {
        name: 'Phoenix',
        language: 'Elixir'
      }]
    };
  },
  methods: {
    clear() {
      this.training.employee_id = "";
      this.training.training_center_name = "";
      this.training.training_name = "";
      this.training.training_score = "";
      this.training.training_feedback = "";
      this.training.training_strt_date = "";
      this.training.training_end_date = "";
      this.training.description = "";
    },
    nameWithLang({
      name,
      language
    }) {
      return `${name} â€” [${language}]`;
    },
    getEmployeeList() {
      this.axios.get(this.backend_url + 'employeeList').then(res => {
        //console.log(res.data.employeeList);
        this.employeeList = res.data.employeeList;
      }).catch(e => {
        console.log(e);
      });
    },
    getpromotionList() {
      this.axios.get(this.backend_url + 'getTrainingList?user_id=' + this.storageData.id).then(res => {
        console.log(res.data.list);
        this.promotionList = res.data.list;
        setTimeout(function () {
          $("#datatable").DataTable({
            "processing": true
          });
        }, 100);
      }).catch(e => {
        console.log(e);
      });
    },
    deletebutton(id) {
      this.axios.delete(this.backend_url + 'deleteTransferRecord/' + id).then(res => {
        console.log('Delete Successfully', res);
        this.getpromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Delete Successfully!!`);
      });
    },
    viewDetails(id) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificUserTransferRecord/' + id).then(response => {
        console.log(response);
        this.transferRecordById = response.data.data;
        console.log(this.transferRecordById.transfer_type);
        this.tansfer.id = id;
        this.tansfer.employee_name = this.transferRecordById.employee_name;
        this.tansfer.employee_id = this.transferRecordById.employee_id;
        this.tansfer.transfer_type = this.transferRecordById.transfer_type;
        this.tansfer.transfer_order = this.transferRecordById.transfer_order;
        this.tansfer.transfer_order_number = this.transferRecordById.transfer_order_number;
        this.tansfer.to_office = this.transferRecordById.to_office;
        this.tansfer.from_office = this.transferRecordById.from_office;
        this.tansfer.to_designation = this.transferRecordById.to_designation;
        this.tansfer.from_designation = this.transferRecordById.from_designation;
        this.tansfer.transfer_date = this.transferRecordById.transfer_date;
        this.tansfer.join_date = this.transferRecordById.join_date;
        this.tansfer.transfer_letter = this.transferRecordById.transfer_letter;
        this.tansfer.to_department = this.transferRecordById.to_department;
        this.tansfer.from_department = this.transferRecordById.from_department;
        this.edit = false;
      }).catch(e => {
        console.log(e);
      });
    },
    getDepartmentList() {
      this.axios.get(this.backend_url + 'getDepartment').then(res => {
        // console.log(res.data.data);
        this.departments = res.data.data;
      });
    },
    getTransfertype() {
      this.axios.get(this.backend_url + 'transferType').then(res => {
        this.transferType = res.data.list;
        // console.log('fddsfsdf');
        // console.log(this.transferType[0].title);
      });
    },

    getUser() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        // this.tansfer.employee_id = this.user
        // console.log(this.storageData.id);
      });
    },

    getDesignationList() {
      this.axios.get(this.backend_url + 'getDesignationMgt').then(res => {
        this.designations = res.data.data;
        // console.log(this.designations);
      });
    },

    getOffice() {
      this.axios.get(this.backend_url + `getOfficeMgt`).then(response => {
        this.office = response.data.list;
        // console.log(this.office);
      });
    },

    activestatus(id) {
      this.axios.patch(this.backend_url + `activeTrainingRecord/${id}`).then(response => {
        console.log(response);
        this.getpromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.success(`Active status Successfully!!`);
      }).catch(error => {
        console.error(error);
      });
    },
    inactivestatus(id) {
      this.axios.patch(this.backend_url + `inactiveTrainingRecord/${id}`).then(response => {
        console.log(response);
        this.getpromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Status Cancel!!`);
      }).catch(error => {
        console.error(error);
      });
    },
    saveTraining() {
      // this.training.employee_id = this.storageData.id;
      this.edit = false;
      this.axios.post(this.backend_url + 'addTrainingRecord', this.training).then(res => {
        console.log(res);
        $("#exampleModal").modal('hide');
        this.getpromotionList();
        $('#datatable').DataTable().destroy();
        this.$toast.success(`Data Added Successfully!!`);
      }).catch(error => {
        console.error("An error occurred while updating the training record:", error);
        this.$toast.error(error);
      });
    }
  },
  created() {
    this.getEmployeeList();
    this.getpromotionList();
    this.getDepartmentList();
    this.getDesignationList();
    this.getTransfertype();
    this.getUser();
    this.getOffice();
  },
  watch: {}
};
