import "jquery/dist/jquery.min.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import $ from "jquery";
import { Form, Field } from 'vee-validate';
import * as Yup from "yup";

// DataTable.use(DataTablesCore);
export default {
  components: {
    // DataTable,
    Form,
    Field
  },
  data() {
    const storageData = JSON.parse(localStorage.getItem('user'));
    const schema = Yup.object().shape({
      transfer_type: Yup.string().required("Transfer Type is required"),
      to_department: Yup.string().required("To Department is required"),
      from_department: Yup.string().required("From Department is required"),
      transfer_date: Yup.date().required("Transfer Date is required").typeError("Invalid date format"),
      transfer_order_number: Yup.string().required("Transfer Order Number is required"),
      to_designation: Yup.string().required("To Designation is required"),
      from_designation: Yup.string().required("From Designation is required"),
      join_date: Yup.date().required("Join Date is required").typeError("Invalid date format"),
      to_office: Yup.string().required("To Office is required"),
      from_office: Yup.string().required("From Office is required"),
      transfer_order: Yup.string().required("Transfer Order is required"),
      transfer_letter: Yup.string().required("Transfer Letter is required")
    });
    return {
      schema,
      backend_url: process.env.VUE_APP_API_URL,
      storageData,
      user: "",
      departments: "",
      designations: "",
      transferType: "",
      office: "",
      edit: false,
      pdf: [],
      url: this.backendUrl + "/api/users",
      basicInfo: {
        name: '',
        mobileNumber: '',
        email: '',
        designation: '',
        designation_id: '',
        image: ''
      },
      transferRecordById: [],
      transferList1: [],
      tansfer: {
        employee_id: "",
        transfer_type: "",
        transfer_order: "",
        transfer_order_number: "",
        to_office: "",
        from_office: "",
        to_department: "",
        from_department: "",
        to_designation: "",
        from_designation: "",
        transfer_date: "",
        join_date: "",
        transfer_letter: ""
      }
    };
  },
  computed: {},
  methods: {
    handleFileChange(event) {
      const selectedFile = event.target.files[0];
      this.tansfer.transfer_letter = selectedFile;
    },
    clear() {
      this.tansfer.transfer_type = "";
      this.tansfer.transfer_order = "";
      this.tansfer.transfer_order_number = "";
      this.tansfer.to_office = "";
      this.tansfer.from_office = "";
      this.tansfer.to_designation = "";
      this.tansfer.from_designation = "";
      this.tansfer.transfer_date = "";
      this.tansfer.join_date = "";
      this.tansfer.transfer_letter = "";
      this.tansfer.to_department = "";
      this.tansfer.from_department = "";
    },
    editbutton(id) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificUserTransferRecord/' + id).then(response => {
        this.transferRecordById = response.data.data;
        console.log(this.transferRecordById);
        this.tansfer.id = id;
        this.tansfer.employee_name = this.transferRecordById.employee_name;
        this.tansfer.employee_id = this.transferRecordById.employee_id;
        this.tansfer.transfer_type = this.transferRecordById.transfer_type;
        this.tansfer.transfer_order = this.transferRecordById.transfer_order;
        this.tansfer.transfer_order_number = this.transferRecordById.transfer_order_number;
        this.tansfer.to_office = this.transferRecordById.to_office;
        this.tansfer.from_office = this.transferRecordById.from_office;
        this.tansfer.to_designation = this.transferRecordById.to_designation;
        this.tansfer.from_designation = this.transferRecordById.from_designation;
        this.tansfer.transfer_date = this.transferRecordById.transfer_date;
        this.tansfer.join_date = this.transferRecordById.join_date;
        this.tansfer.transfer_letter = this.transferRecordById.transfer_letter;
        this.tansfer.to_department = this.transferRecordById.to_department;
        this.tansfer.from_department = this.transferRecordById.from_department;
        this.edit = true;
      }).catch(e => {
        console.log(e);
      });
    },
    deletebutton(id) {
      this.axios.delete(this.backend_url + 'deleteTransferRecord/' + id).then(res => {
        console.log('Delete Successfully', res);
        this.getTransferList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Delete Successfully!!`);
      });
    },
    viewDetails(id) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificUserTransferRecord/' + id).then(response => {
        console.log(response);
        this.transferRecordById = response.data.data;
        console.log(this.transferRecordById.transfer_type);
        this.tansfer.id = id;
        this.tansfer.employee_name = this.transferRecordById.employee_name;
        this.tansfer.employee_id = this.transferRecordById.employee_id;
        this.tansfer.transfer_type = this.transferRecordById.transfer_type;
        this.tansfer.transfer_order = this.transferRecordById.transfer_order;
        this.tansfer.transfer_order_number = this.transferRecordById.transfer_order_number;
        this.tansfer.to_office = this.transferRecordById.to_office;
        this.tansfer.from_office = this.transferRecordById.from_office;
        this.tansfer.to_designation = this.transferRecordById.to_designation;
        this.tansfer.from_designation = this.transferRecordById.from_designation;
        this.tansfer.transfer_date = this.transferRecordById.transfer_date;
        this.tansfer.join_date = this.transferRecordById.join_date;
        this.tansfer.transfer_letter = this.transferRecordById.transfer_letter;
        this.tansfer.to_department = this.transferRecordById.to_department;
        this.tansfer.from_department = this.transferRecordById.from_department;
        this.edit = false;
      }).catch(e => {
        console.log(e);
      });
    },
    getTransferList() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        this.tansfer.employee_id = this.user;
        // console.log(this.storageData.id);

        this.axios.get(this.backend_url + 'specificUserTransferRecordByEmployeeId/' + this.user).then(response => {
          this.transferList1 = response.data;
          console.log(response.data.data[0].transfer_letter);
          // Initialize DataTable here
          this.pdf = this.base_url_for_resource + 'transferLetters/' + response.data.data.transfer_letter;
          console.log(this.pdf);
          setTimeout(function () {
            $("#datatable").DataTable({
              "processing": true
            });
          }, 100);
        }).catch(e => {
          console.log(e);
        });
      });
    },
    getDepartmentList() {
      this.axios.get(this.backend_url + 'getDepartment').then(res => {
        // console.log(res.data.data);
        this.departments = res.data.data;
      });
    },
    getDesignationList() {
      this.axios.get(this.backend_url + 'getDesignationMgt').then(res => {
        this.designations = res.data.data;
        // console.log(this.designations);
      });
    },

    getTransfertype() {
      this.axios.get(this.backend_url + 'transferType').then(res => {
        this.transferType = res.data.list;
        // console.log('fddsfsdf');
        // console.log(this.transferType[0].title);
      });
    },

    getUser() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        this.tansfer.employee_id = this.user;
        // console.log(this.storageData.id);
      });
    },

    getOffice() {
      this.axios.get(this.backend_url + `getOfficeMgt`).then(response => {
        this.office = response.data.list;
        // console.log(this.office);
      });
    },

    saveTranfer() {
      let formData = new FormData();
      formData.append('employee_id', this.tansfer.employee_id);
      formData.append('transfer_type', this.tansfer.transfer_type);
      formData.append('transfer_order', this.tansfer.transfer_order);
      formData.append('transfer_order_number', this.tansfer.transfer_order_number);
      formData.append('to_office', this.tansfer.to_office);
      formData.append('from_office', this.tansfer.from_office);
      formData.append('to_designation', this.tansfer.to_designation);
      formData.append('from_designation', this.tansfer.from_designation);
      formData.append('transfer_date', this.tansfer.transfer_date);
      formData.append('join_date', this.tansfer.join_date);
      formData.append('transfer_letter', this.tansfer.transfer_letter);
      formData.append('to_department', this.tansfer.to_department);
      formData.append('from_department', this.tansfer.from_department);
      try {
        // this.tansfer.employee_id = this.storageData.id;
        this.edit = false;
        this.axios.post(this.backend_url + 'addTransferRecord', formData).then(res => {
          console.log(res);
          this.getTransferList();
          $("#exampleModal").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Add Data Successfully!`);
        }).catch(error => {
          console.error("An error occurred while saving the transfer record:", error);
          this.$toast.error(`Failed to save transfer record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to save transfer record.`);
      }
    },
    EditTranfer() {
      try {
        this.axios.post(this.backend_url + `updateTransferRecord/${this.tansfer.id}`, this.tansfer).then(res => {
          console.log(res);
          this.getTransferList();
          $("#exampleModal").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Update Data Successfully!`);
        }).catch(error => {
          console.error("An error occurred while updating the transfer record:", error);
          this.$toast.error(`Failed to update transfer record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to update transfer record.`);
      }
    }
  },
  created() {
    this.getTransferList();
    this.getDepartmentList();
    this.getDesignationList();
    this.getTransfertype();
    this.getUser();
    this.getOffice();
  },
  mounted() {},
  watch: {}
};
