          data.append('attendant_passport_place_of_issue', formData.attendant_passport_place_of_issue);
          data.append('attendant_passport_date_of_issue', formData.attendant_passport_date_of_issue);
          data.append('attendant_passport_date_of_expiry', formData.attendant_passport_date_of_expiry);
        }
      }

      this.axios.post(this.base_url + '/api/addGuest', data, config).then(function (response) {
        console.log(response);
        resetForm();
        createToast('Registration Add Successfully !', {
          position: 'top-center',
          transition: 'bounce',
          timeout: 1500,
          type: 'success'
        });
        setTimeout(function () {
          location.reload();
        }, 1000); //$('#modalForAddingNewGuest').modal('hide');
      }).catch(function (err) {
        console.log(err);
        $("#registerBtn").removeAttr("disabled");
      });
    },

    // For Open Add New Guest Moadal
    openAddNewGuestModal() {
      this.editImageFlag = false;
      this.$refs.form.resetForm();
      $('#modalForAddingNewGuest').modal('show');
    },

    pickAttendantPhotoFile(ev) {
      this.attendent_photo = ev.target.files[0];
    },

    pickPassportFile(ev) {
      this.passport = ev.target.files[0];
    },

    pickAttendantPassportFile(ev) {
      this.attendent_passport = ev.target.files[0];
    },

    pickVaccineCertificateFile(ev) {
      this.covid19c = ev.target.files[0];
    },

    //Croping image
    cropImage(e) {
      console.log(e);
      let reader = new FileReader();
      console.log(reader);
      this.image = this.cropData;
      var pic = this.image;

      if (this.editImageFlag) {
        $('#prvImgEdit').attr('src', pic);
      }

      $('#prvImg').attr('src', pic);
      $('#cropModal').modal('hide');
      setTimeout(function () {
        $('body').css({
          'height': 'auto',
          'padding-right': '17px'
        });
        $('body').addClass('modal-open');
      }, 500);
    },

    //Croping image params
    changes({
      coordinates,
      canvas
    }) {
      console.log(coordinates, canvas);
      this.cropData = canvas.toDataURL(this.image);
    },

    // Image
    onFileUpload(e) {
      let file = e.target.files[0];
      let reader = new FileReader();
      this.img = '';

      if (file["size"] < 10111775) {
        reader.onloadend = file => {
          console.log(file);
          this.img = reader.result;
        };

        reader.readAsDataURL(file);
      } else {
        alert("File size can not be bigger than 10 MB");
      }

      $('#cropModal').modal('show');
    },

    print() {
      this.$htmlToPaper('printApplicationContent');
    },

    // For Clearing Mail Modal
    onClear() {
      this.mailTo = '';
      this.mailSubject = '';
      this.mailBody = '';
    },

    // For Reply Button
    onReply(registeredGuest) {
      this.mailTo = registeredGuest.email;
      this.registeredGuestName = registeredGuest.full_name;
      this.eventId = registeredGuest.event_id;
      $(document).ready(function () {
        $('#exampleModalCenterForSendingMail').modal('show');
      });
    },

    // For Sending Mail
    onSend() {
      this.spinnerOn = true;
      $(document).ready(function () {
        $('#exampleModalCenterForSendingMail').modal('hide');
      });
      this.axios.post(this.base_url + '/api/send-reply', {
        subject: this.mailSubject,
        //title: this.mailTitle,
        message_body: this.mailBody,
        email: this.mailTo,
        name: this.registeredGuestName,
        event_id: this.eventId
      }).then(res => {
        console.log(res);
        createToast('Mail Sent Successfully !', {
          position: 'top-center',
          transition: 'bounce',
          timeout: 1500,
          type: 'success'
        });
        this.spinnerOn = false;
        this.onClear();
      }).catch(err => {
        console.log(err);
        createToast('Mail Sent Successfully !', {
          position: 'top-center',
          transition: 'bounce',
          timeout: 1500,
          type: 'success'
        });
        this.spinnerOn = false;
        this.onClear();
      });
    },

    // For Fatching All Registered Guest Information
    getAllRegisteredGuest() {
      $('#datatable').DataTable().destroy();
      this.axios.get(this.base_url + "/api/getGuest").then(res => {
        this.registeredGuests = res.data.userList;
        this.eventTitle = res.data.activeEvent.title;
        $(document).ready(function () {
          $('#datatable').DataTable();
        });
      }).catch(err => {
        console.log(err);
      });
    },

    // Show Specific Guest Information
    onShow(registeredGuest) {
      this.registeredGuestInModdal = registeredGuest;
      $(document).ready(function () {
        $('#exampleModalCenter').modal('show');
      });
    },

    //Data Return
    formatDate(value) {
      return moment(value).format('DD MMMM YYYY');
    },

    onEdit(data) {
      this.editImageFlag = true;
      console.log(data);
      this.editData = data;
      console.log(this.editData.id);

      if (data.photo) {
        this.editImage = this.base_url + '/public/uploads/images/photos/' + data.photo;
      }

      $('#modalForUpdateGuest').modal('show');
    },

    onDelete(id) {
      this.$swal.fire({
        text: "Are you sure to delete ?",
        icon: "error",
        cancelButtonText: "Cancel",
        confirmButtonText: "Yes, Confirm Delete",
        showCancelButton: true
      }).then(result => {
        if (result["isConfirmed"]) {
          this.axios.delete(this.base_url + '/api/deleteGuest/' + id).then(function (response) {
            console.log(response);
            createToast('Guest Deleted Successfully !', {
              position: 'top-center',
              transition: 'bounce',
              timeout: 1500,
              type: 'danger'
            });
            setTimeout(function () {
              location.reload();
            }, 2000);
          }).catch(function (err) {
            console.log(err);
          });
        }
      });
    }

  },

  created() {
    this.getAllRegisteredGuest();
  }

};
