      children:[
        {
          path: '/registration-info',
          name: 'Registration Information',
          component: () => import('../components/RegInfo/RegInfo.vue'),
        }
      ]
    },

    {
      path: '/galleries',
      name: 'galleries',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/galleries',
          name: 'galleries',
          component: () => import('../components/Gallery/Gallery.vue'),
        }
      ]
    },

    {
      path: '/pages',
      name: 'pages',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/pages',
          name: 'pages',
          component: () => import('../components/Page/Page.vue'),
        }
      ]
    },

    {
      path: '/schedules',
      name: 'Schedules',
