      children:[
        {
          path: '/schedules',
          name: 'Schedules',
          component: () => import('../components/Schedule/Schedule.vue'),
        }
      ]
    },

    {
      path: '/event_registrations',
      name: 'event registrations',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/event_registrations',
          name: 'event_registrations',
          component: () => import('../components/EventRegistration/EventRegistration.vue'),
        }
      ]
    },

    {
      path: '/speakers',
      name: 'Speakers',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/speakers',
          name: 'Speakers',
          component: () => import('../components/Speaker/Speaker.vue'),
        }
      ]
    },

    {
      path: '/marketplace',
      name: 'marketplace',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/marketplace',
          name: 'marketplace',
          component: () => import('../components/MarketPlace/MarketPlace.vue'),
        }
      ]
    },
    {
