        }
      ]
    },
    {
      path: '/video',
      name: 'video',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/video',
          name: 'video',
          component: () => import('../components/Video/Video.vue'),
        }
      ]
    },
    
    {
      path: '/partner',
      name: 'Partner',
      component: () => import('../Layout/Fullpagelayout.vue'),
      children:[
        {
          path: '/partner',
          name: 'Partner',
