import axios from 'axios';
import router from "../../router";

axios.interceptors.request.use(
    config => {
        const storageData = JSON.parse(localStorage.getItem('user'));
        if (storageData) {
            config.headers['Authorization'] = storageData.token_type +' '+ storageData.access_token; 
        }
        return config;
    },
    error => {
        Promise.reject(error)
        //return error;
    });


// Add a response interceptor
axios.interceptors.response.use(
  async function (response) {
    return response;
  },
  async function (error) {
   // await console.log("test error", error.response.status);
    //await console.log("test error", error.response.data.message);
    if (error.response.status === 401) {
      router.push("/");
      return Promise.reject(error);
    }
    return Promise.reject(error);
  }
);

// initial state
const state = () => ({
  speakers: {},
  speakerPaginatedData: {},
  createdSpeakerData: {},
  messageForSpeaker: null,
  speaker: null,
  updatedSpeakerData: null,
  activeEventTitle:''



});


// getters
const getters = {
  speakers: state => state.speakers,
  speakerPaginatedData: state => state.speakerPaginatedData,
  createdSpeakerData: state => state.createdSpeakerData,
  messageForSpeaker: state => state.messageForSpeaker,
  speaker: state => state.speaker,
  updatedSpeakerData: state => state.updatedSpeakerData,
  activeEventTitle: state => state.activeEventTitle

};

// actions
const actions = {
  async fetchAllSpeaker({ commit }, query = null) {
    let page = 1;
    let search = '';
    if(query !== null){
      page = query?.page || 1;
      search = query?.search || '';
    }

    commit('setSpeakerIsLoading', true);
    let url = `${process.env.VUE_APP_API_URL}getSpeaker`;
    if (search === null) {
      url = `${url}`;
    } else {
      url = `${process.env.VUE_APP_API_URL}speaker/search?search=${search}&page=${page}`
    }

    await axios.get(url)
      .then(res => {
        const speaker = res.data.data;
        commit('setSpeaker', speaker);
        const pagination = {
          total: res.data.data.total,  // total number of elements or items
          per_page: res.data.data.per_page, // items per page
          current_page: res.data.data.current_page, // current page (it will be automatically updated when users clicks on some page number).
          total_pages: res.data.data.last_page // total pages in record
        }
        res.data.data.pagination = pagination;


        //console.log(res.data.eventTitle);
        commit('setActiveEventTitle',res.data.eventTitle);
        commit('setSpeakerPaginated', res.data.data);

        commit('setSpeakerIsLoading', false);
      }).catch(err => {
        commit('setSpeakerIsLoading', false);
        commit('setMessage', err.response.data.message);

      });
  },

  async fetchDetailSpeaker({ commit }, id) {
    commit('setSpeakerIsLoading', true);
    await axios.get(`${process.env.VUE_APP_API_URL}getSpeakerDetail/${id}`)
      .then(res => {
        commit('setSpeakerDetail', res.data.data);
        commit('setSpeakerIsLoading', false);
      }).catch(err => {
        commit('setSpeakerIsLoading', false);
        commit('setMessage', err.response.data.message);
      });
  },

  async storeSpeaker({ commit }, speaker) {
    commit('setSpeakerIsCreating', true);
    commit('setMessage', null);
    await axios.post(`${process.env.VUE_APP_API_URL}addSpeaker`, speaker)
      .then(res => {
        commit('saveNewSpeaker', res.data);
        commit('setSpeakerIsCreating', false);
      }).catch((err) => {
        commit('setSpeakerIsCreating', false);
        if (err.response != null){ 
          commit('setMessage', err.response.data.message);
        }

      });
  },

  async updateSpeaker({ commit }, speaker) {
    commit('setSpeakerIsUpdating', true);
    await axios.post(`${process.env.VUE_APP_API_URL}updateSpeaker/${speaker.id}?_method=PUT`, speaker)
      .then(res => {
        commit('setSpeakerIsUpdating', false);
        commit('saveUpdatedSpeaker', res.data.data);
      }).catch(err => {
        commit('setSpeakerIsUpdating', false);
        if (err.response != null){ 
          commit('setMessage', err.response.data.message);
        }
      });
  },

  async deleteSpeaker({ commit }, id) {
    commit('setSpeakerIsDeleting', true);
    await axios.delete(`${process.env.VUE_APP_API_URL}deleteSpeaker/${id}`)
      .then(res => {
        commit('setDeleteSpeaker', res.data.data.id);
        commit('setSpeakerIsDeleting', false);
      }).catch(err => {
        commit('setSpeakerIsDeleting', false);
        if (err.response != null){ 
          commit('setMessage', err.response.data.message);
        }
      });
  },

  updateSpeakerInput({ commit }, e) {
    commit('setSpeakerDetailInput', e);
  }


}

// mutations
const mutations = {

  setActiveEventTitle: (state,activeEventTitle) => {
    state.activeEventTitle = activeEventTitle;
  },

  setSpeaker: (state, speaker) => {
    state.speakers = speaker
  },

  setSpeakerPaginated: (state, speakerPaginatedData) => {
    state.speakerPaginatedData = speakerPaginatedData;
  },

  setSpeakerDetail: (state, speaker) => {
    state.speaker = speaker
  },

  setDeleteSpeaker: (state, id) => {
    let pData = state.speakerPaginatedData.data;
    let index = pData.findIndex(pData => pData.id == id);
    state.speakerPaginatedData.data.splice(index, 1);
    state.speakerPaginatedData.data.filter(x => x.id !== id);
  },

  setSpeakerDetailInput: (state, e) => {
    let speaker = state.speaker;
    speaker[e.target.name] = e.target.value;
    state.speaker = speaker
  },

  saveNewSpeaker: (state, speakers) => {
    //console.log(schedules.data);
    state.createdSpeakerData = speakers.data;
  },

  saveUpdatedSpeaker: (state, schedule) => {
    state.updatedSpeakerData = schedule;
    console.log(schedule);
    console.log(state.updatedSpeakerData );
  },

  setSpeakerIsLoading(state, isLoading) {
    state.isLoading = isLoading
  },

  setSpeakerIsCreating(state, isCreating) {
    state.isCreating = isCreating
  },

  setSpeakerIsUpdating(state, isUpdating) {
    state.isUpdating = isUpdating
    state.updatedData = null;
    console.log(state.updatedData );
  },

  setSpeakerIsDeleting(state, isDeleting) {
    state.isDeleting = isDeleting
  },

  fromActionStatus(state, status) {
    state.formAction = status
  },

  setMessage(state, messageForSpeaker){
    state.messageForSpeaker = messageForSpeaker
  },

}

export default {
  // namespaced: true,
  state,
  getters,
  actions,
  mutations
}
