import "jquery/dist/jquery.min.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";
import $ from "jquery";
import { Form, Field } from 'vee-validate';
import * as Yup from "yup";

// DataTable.use(DataTablesCore);
export default {
  components: {
    // DataTable,
    Form,
    Field
  },
  data() {
    const storageData = JSON.parse(localStorage.getItem('user'));
    const schema = Yup.object().shape({
      transfer_type: Yup.string().required("Transfer Type is required"),
      to_department: Yup.string().required("To Department is required"),
      from_department: Yup.string().required("From Department is required"),
      transfer_date: Yup.date().required("Transfer Date is required").typeError("Invalid date format"),
      transfer_order_number: Yup.string().required("Transfer Order Number is required"),
      to_designation: Yup.string().required("To Designation is required"),
      from_designation: Yup.string().required("From Designation is required"),
      join_date: Yup.date().required("Join Date is required").typeError("Invalid date format"),
      to_office: Yup.string().required("To Office is required"),
      from_office: Yup.string().required("From Office is required"),
      transfer_order: Yup.string().required("Transfer Order is required"),
      transfer_letter: Yup.string().required("Transfer Letter is required")
    });
    return {
      schema,
      backend_url: process.env.VUE_APP_API_URL,
      storageData,
      user: "",
      departments: "",
      designations: "",
      transferType: "",
      office: "",
      pdf: [],
      url: this.backendUrl + "/api/users",
      basicInfo: {
        name: '',
        mobileNumber: '',
        email: '',
        designation: '',
        designation_id: '',
        image: ''
      },
      transferRecordById: [],
      AcrList: [],
      // <td>{{ item.employee_id }}</td>
      //                         <td>{{ item.emp_name }}</td>
      //                         <td>{{ item.designation }}</td>
      //                         <td>{{ item.department }}</td>
      //                         <td>{{ item.office_name }}</td>
      //                         <td>{{ item.rack_number }}</td>
      //                         <td>{{ item.bin_number }}</td>
      //                         <td>{{ item.file }}</td>
      //                         <td>{{ item.file_number }}</td>
      //                         <td>{{ item.acr_year }}</td>
      //                         <td>{{ item.score }}</td>
      Acr: {
        employee_id: "",
        emp_name: "",
        designation: "",
        department: "",
        office_name: "",
        rack_number: "",
        bin_number: "",
        file: "",
        file_number: "",
        acr_year: "",
        score: ""
      },
      editAcr: {
        employee_id: "",
        emp_name: "",
        designation: "",
        department: "",
        office_name: "",
        rack_number: "",
        bin_number: "",
        file: "",
        file_number: "",
        acr_year: "",
        score: ""
      }
    };
  },
  computed: {},
  methods: {
    handleFileChange(event) {
      const selectedFile = event.target.files[0];
      this.Acr.transfer_letter = selectedFile;
    },
    edithandleFileChange(event) {
      const selectedFile = event.target.files[0];
      this.editAcr.transfer_letter = selectedFile;
    },
    editbutton(id) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificUserTransferRecord/' + id).then(response => {
        this.transferRecordById = response.data.data;
        console.log(this.transferRecordById);
        this.editAcr.id = id;
        this.editAcr.employee_name = this.transferRecordById.employee_name;
        this.editAcr.employee_id = this.transferRecordById.employee_id;
        this.editAcr.transfer_type = this.transferRecordById.transfer_type;
        this.editAcr.transfer_order = this.transferRecordById.transfer_order;
        this.editAcr.transfer_order_number = this.transferRecordById.transfer_order_number;
        this.editAcr.to_office = this.transferRecordById.to_office;
        this.editAcr.from_office = this.transferRecordById.from_office;
        this.editAcr.to_designation = this.transferRecordById.to_designation;
        this.editAcr.from_designation = this.transferRecordById.from_designation;
        this.editAcr.transfer_date = this.transferRecordById.transfer_date;
        this.editAcr.join_date = this.transferRecordById.join_date;
        this.editAcr.transfer_letter = this.transferRecordById.transfer_letter;
        this.editAcr.to_department = this.transferRecordById.to_department;
        this.editAcr.from_department = this.transferRecordById.from_department;
        this.edit = true;
      }).catch(e => {
        console.log(e);
      });
    },
    deletebutton(id) {
      this.axios.delete(this.backend_url + 'deleteTransferRecord/' + id).then(res => {
        console.log('Delete Successfully', res);
        this.getAcrList();
        $('#datatable').DataTable().destroy();
        this.$toast.error(`Delete Successfully!!`);
      });
    },
    viewDetails(id) {
      console.log(id);
      this.axios.get(this.backend_url + 'specificUserTransferRecord/' + id).then(response => {
        console.log(response);
        this.transferRecordById = response.data.data;
        console.log(this.transferRecordById.transfer_type);
        this.Acr.id = id;
        this.Acr.employee_name = this.transferRecordById.employee_name;
        this.Acr.employee_id = this.transferRecordById.employee_id;
        this.Acr.transfer_type = this.transferRecordById.transfer_type;
        this.Acr.transfer_order = this.transferRecordById.transfer_order;
        this.Acr.transfer_order_number = this.transferRecordById.transfer_order_number;
        this.Acr.to_office = this.transferRecordById.to_office;
        this.Acr.from_office = this.transferRecordById.from_office;
        this.Acr.to_designation = this.transferRecordById.to_designation;
        this.Acr.from_designation = this.transferRecordById.from_designation;
        this.Acr.transfer_date = this.transferRecordById.transfer_date;
        this.Acr.join_date = this.transferRecordById.join_date;
        this.Acr.transfer_letter = this.transferRecordById.transfer_letter;
        this.Acr.to_department = this.transferRecordById.to_department;
        this.Acr.from_department = this.transferRecordById.from_department;
        this.edit = false;
      }).catch(e => {
        console.log(e);
      });
    },
    getAcrList() {
      this.axios.get(this.backend_url + 'getAcr').then(res => {
        console.log(res.data.list);
        this.AcrList = res.data.list;
        setTimeout(function () {
          $("#datatable").DataTable({
            "processing": true
          });
        }, 100);
      }).catch(e => {
        console.log(e);
      });
    },
    getDepartmentList() {
      this.axios.get(this.backend_url + 'getDepartment').then(res => {
        // console.log(res.data.data);
        this.departments = res.data.data;
      });
    },
    getDesignationList() {
      this.axios.get(this.backend_url + 'getDesignationMgt').then(res => {
        this.designations = res.data.data;
        // console.log(this.designations);
      });
    },

    getTransfertype() {
      this.axios.get(this.backend_url + 'transferType').then(res => {
        this.transferType = res.data.list;
        // console.log('fddsfsdf');
        // console.log(this.transferType[0].title);
      });
    },

    getUser() {
      this.axios.get(this.backend_url + `users/${this.storageData.id}`).then(res => {
        this.user = res.data.userDetail.employee_id;
        // console.log(this.user);
        this.Acr.employee_id = this.user;
        // console.log(this.storageData.id);
      });
    },

    getOffice() {
      this.axios.get(this.backend_url + `getOfficeMgt`).then(response => {
        this.office = response.data.list;
        // console.log(this.office);
      });
    },

    saveTranfer() {
      let formData = new FormData();
      formData.append('employee_id', this.Acr.employee_id);
      formData.append('transfer_type', this.Acr.transfer_type);
      formData.append('transfer_order', this.Acr.transfer_order);
      formData.append('transfer_order_number', this.Acr.transfer_order_number);
      formData.append('to_office', this.Acr.to_office);
      formData.append('from_office', this.Acr.from_office);
      formData.append('to_designation', this.Acr.to_designation);
      formData.append('from_designation', this.Acr.from_designation);
      formData.append('transfer_date', this.Acr.transfer_date);
      formData.append('join_date', this.Acr.join_date);
      formData.append('transfer_letter', this.Acr.transfer_letter);
      formData.append('to_department', this.Acr.to_department);
      formData.append('from_department', this.Acr.from_department);
      try {
        // this.Acr.employee_id = this.storageData.id;
        this.axios.post(this.backend_url + 'addTransferRecord', formData).then(res => {
          console.log(res);
          this.getAcrList();
          $("#exampleModal").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Add Data Successfully!`);
          this.$refs.form.resetForm();
        }).catch(error => {
          console.error("An error occurred while saving the transfer record:", error);
          this.$toast.error(`Failed to save transfer record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to save transfer record.`);
      }
    },
    EditTranfer() {
      let formData = new FormData();
      formData.append('employee_id', this.editAcr.employee_id);
      formData.append('transfer_type', this.editAcr.transfer_type);
      formData.append('transfer_order', this.editAcr.transfer_order);
      formData.append('transfer_order_number', this.editAcr.transfer_order_number);
      formData.append('to_office', this.editAcr.to_office);
      formData.append('from_office', this.editAcr.from_office);
      formData.append('to_designation', this.editAcr.to_designation);
      formData.append('from_designation', this.editAcr.from_designation);
      formData.append('transfer_date', this.editAcr.transfer_date);
      formData.append('join_date', this.editAcr.join_date);
      formData.append('transfer_letter', this.editAcr.transfer_letter);
      formData.append('to_department', this.editAcr.to_department);
      formData.append('from_department', this.editAcr.from_department);
      try {
        this.axios.post(this.backend_url + `updateTransferRecord/${this.editAcr.id}`, formData).then(res => {
          console.log(res);
          this.getAcrList();
          $("#exampleModal1").modal('hide');
          $('#datatable').DataTable().destroy();
          this.$toast.success(`Update Data Successfully!`);
        }).catch(error => {
          console.error("An error occurred while updating the transfer record:", error);
          this.$toast.error(`Failed to update transfer record.`);
        });
      } catch (error) {
        console.error("An error occurred:", error);
        this.$toast.error(`Failed to update transfer record.`);
      }
    }
  },
  created() {
    this.getAcrList();
    this.getDepartmentList();
    this.getDesignationList();
    this.getTransfertype();
    this.getUser();
    this.getOffice();
